def radix_sort(arr):

    d = len(str(max(arr)))
    for i in range(0, d):
        new_arr = [0] * len(arr)
        for j in range(0, len(arr)):
            if arr[j] % 10 ** i == 0:
                new_arr[j] = 0
            else:
                new_arr[j] = arr[j] % 10 ** i
        #insertion_sort(new_arr)
        for j in range(0, len(arr)):
            if new_arr[j] == 0:
                arr[j] = 0
            else:
                arr[j] = new_arr[j] / 10 ** i
    return arr
